import react from 'react';
import '../card.css';
import React, { useState, useCallback } from "react";
    export const  Card = props => {
        const [isPhotoGrapherCardOpen, setIsPhotoGrapherCardOpen] = useState(false);
        const [showData, setIsShowData] = useState(true);
         const openPhotographerSection = useCallback(i => {
            setIsPhotoGrapherCardOpen(true);
            console.log(isPhotoGrapherCardOpen)
            childToParent(false)
          }, []);
          const childToParent = (childdata) => {
            setIsShowData(false);
          }
          const closePhotographerSection = useCallback(index => {
            setIsPhotoGrapherCardOpen(false);
            console.log(isPhotoGrapherCardOpen)
          }, []);
    return (<>
    {showData && !isPhotoGrapherCardOpen ?
        <div className='card-container'>
            <div className='image-container'>
                <img src={props.imageUrl} alt='' />
            </div>
            <div className='card-content'>
                <div className='card-title'>
                    <h3>{props.title}</h3>
                </div>
                <div className='card-body'>
                    <p>{props.body}</p>
                </div>
            </div>
            {isPhotoGrapherCardOpen ?(
            <div className='btn1'>
                <a
                    href='#photographersCard'
                    className='btn btn-custom-card btn-lg page-scroll'
                    onClick={() => closePhotographerSection()}
                >
                    View less
                </a>{' '}
            </div>

          ):<div className='btn1'>
          <a
              href='#photographersCard'
              className='btn btn-custom-card btn-lg page-scroll'
              onClick={(index) => openPhotographerSection(props.index)}
          >
              View More
          </a>{' '}
      </div>}
            {isPhotoGrapherCardOpen && (
           <div className='btn1'>
           <a
               href='#photographersCard'
               className='btn btn-custom-card btn-lg page-scroll'
               onClick={() => openPhotographerSection()}
           >
               View More
           </a>{' '}
       </div>
          )}
          {showData && (
           <div className='btn1'>
           <a
               href='#photographersCard'
               className='btn btn-custom-card btn-lg page-scroll'
               onClick={(showData) => openPhotographerSection(showData)}
           >
             <a href="">{showData}</a>
           </a>{' '}
       </div>
          )}
        </div>:<></>}
        </>
    )
}

export default Card;
