import react from 'react';
import '../card.css';
    export const  Card = props => {
        const [isPhotoGrapherCardOpen, setIsPhotoGrapherCardOpen] = useState(false);
        const openPhotographerSection = (index) => {
            setIsPhotoGrapherCardOpen(true);
            console.log(index);
          };
    return (
        <div className='card-container'>
            <div className='image-container'>
                <img src={props.imageUrl} alt='' />
            </div>
            <div className='card-content'>
                <div className='card-title'>
                    <h3>{props.title}</h3>
                </div>
                <div className='card-body'>
                    <p>{props.body}</p>
                </div>
            </div>
            <div className='btn1'>
                <a
                    href='#features'
                    className='btn btn-custom-card btn-lg page-scroll'
                    key={index} onClick={() => openPhotographerSection(index)}
                >
                    View More
                </a>{' '}
            </div>
        </div>
    )
}

export default Card;
